DROP TABLE IF EXISTS `mod_npc_promotion_items`;
CREATE TABLE IF NOT EXISTS `mod_npc_promotion_items` (
  `class` INT(2) UNSIGNED NOT NULL DEFAULT 0,
  `faction` INT(3) UNSIGNED NOT NULL DEFAULT 0,
  `function` VARCHAR(6) NOT NULL DEFAULT '',
  `head` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `neck` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `shoulders` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `body` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `chest` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `waist` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `legs` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `feet` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `wrists` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `hands` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `finger1` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `finger2` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `trinket1` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `trinket2` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `back` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `mainhand` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `offhand` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `ranged` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  `tabard` INT(5) UNSIGNED NOT NULL DEFAULT 0,
  PRIMARY KEY (`class`, `faction`, `function`)
) ENGINE=INNODB DEFAULT CHARSET=utf8;

DELETE FROM `mod_npc_promotion_items`;

UPDATE `mod_npc_promotion_items` SET `class`=11 WHERE `class`=10;

